/* This script adds a new column to the DATATABLE, and populates it with a concatenation of data and HTML formatting */
/* this is not final, as we'll need to customize the script to check for null values */
ALTER TABLE "opengeo"."DATATABLE" ADD COLUMN "popup_HTML" text;
UPDATE "opengeo"."DATATABLE"
SET "popup_HTML" = concat('<div class="popupLead"><div class="popupLeadTitle">',"DATATABLE"."Project_Title",'</div><div class="popupLeadDetails"><b>Country: </b>',"DATATABLE"."Country",'<br><b>Sector: </b>',"DATATABLE"."Sector",'<br><b>Date Added: </b>',"DATATABLE"."Timestamp",'<br><b>Primary Funding Source: </b>',"DATATABLE"."Project_Funding_Source",'<br><b>Project Size (USD): </b>',"DATATABLE"."Project_Size",'<br><b>Status: </b>',"DATATABLE"."Status",'<br><b>Archived: </b>',"DATATABLE"."Archived",'<br><br><b>Description: </b><br>',"DATATABLE"."Project_Description",'<br><br><b>Post Comments: </b><br>',"DATATABLE"."Post_Comments",'<br><br><a href=\"',"DATATABLE"."Link_To_Project",'" target="_blank" onclick=\"javascript:ga(''send'', ''event'', ''External_Link'', ''',"DATATABLE"."Project_Title",'_Lead_Details'', {''nonInteraction'': 1});''">Project Website</a>&nbsp;<br><a href=\"',"DATATABLE"."Business_URL",'" target="_blank" onclick=\"javascript:ga(''send'', ''event'', ''Business_Tab_Link'', ''',"DATATABLE"."Project_Title",'_Lead_Details'', {''nonInteraction'': 1});\">Embassy Website</a><br><a href=\"mailto:',"DATATABLE"."Submitting_Officer_Contact",';\" onclick=\"javascript:ga(''send'', ''event'', ''Contact'', ''',"DATATABLE"."Project_Title",'_Lead_Details'', {''nonInteraction'': 1});\">Contact Embassy</a></font></div></div>');
